package com.tms.services.front.emailNotifyProduct.impl;import com.tms.core.ServersManager;import com.tms.services.front.emailNotifyProduct.EmailNotifyProductService;import com.tms.services.front.emailNotifyProduct.bean.EmailNotifyProduct;import com.tms.services.front.emailNotifyProduct.dao.EmailNotifyProductDao;import org.springframework.stereotype.Service;import javax.annotation.Resource;@Service("emailNotifyProductServiceFront")public class EmailNotifyProductServiceImpl extends        ServersManager<EmailNotifyProduct, EmailNotifyProductDao> implements EmailNotifyProductService {    @Resource(name = "emailNotifyProductDaoFront")    @Override    public void setDao(EmailNotifyProductDao emailNotifyProductDao) {        this.dao = emailNotifyProductDao;    }    @Override	public int selectCount(EmailNotifyProduct ep) {		return dao.selectCount(ep);	}}