package com.tms.services.front.favorite.bean;import java.io.Serializable;import com.tms.services.front.product.bean.Product;public class Favorite extends com.tms.services.common.Favorite implements Serializable {	private static final long serialVersionUID = 1L;	private Product product;	public void clear() {		super.clear();		if(product!=null){			product.clear();		}	}	public Product getProduct() {		return product;	}	public void setProduct(Product product) {		this.product = product;	}	}