package com.tms.services.manage.commentType.impl;import com.tms.core.ServersManager;import com.tms.services.manage.commentType.CommentTypeService;import com.tms.services.manage.commentType.bean.CommentType;import com.tms.services.manage.commentType.dao.CommentTypeDao;import org.springframework.stereotype.Service;import javax.annotation.Resource;@Service("commentTypeServiceManage")public class CommentTypeServiceImpl extends ServersManager<CommentType, CommentTypeDao>		implements CommentTypeService {    @Resource(name = "commentTypeDaoManage")    @Override    public void setDao(CommentTypeDao commentTypeDao) {        this.dao = commentTypeDao;    }    @Override	public int update(CommentType e) {		dao.updateAllN();		super.update(e);		return 1;	}}