package com.tms.services.manage.orderdetail.impl;import java.util.List;import com.tms.core.ServersManager;import com.tms.services.manage.orderdetail.OrderdetailService;import com.tms.services.manage.orderdetail.bean.Orderdetail;import com.tms.services.manage.orderdetail.dao.OrderdetailDao;import com.tms.web.action.manage.report.ReportInfo;import org.springframework.stereotype.Service;import javax.annotation.Resource;@Service("orderdetailServiceManage")public class OrderdetailServiceImpl extends ServersManager<Orderdetail, OrderdetailDao>		implements OrderdetailService {    @Resource(name = "orderdetailDaoManage")    @Override    public void setDao(OrderdetailDao orderdetailDao) {        this.dao = orderdetailDao;    }	@Override	public List<ReportInfo> reportProductSales(Orderdetail orderdetail) {		return dao.reportProductSales(orderdetail);	}}