package com.tms.services.manage.systemlog.impl;import com.tms.core.ServersManager;import com.tms.services.manage.systemlog.SystemlogService;import com.tms.services.manage.systemlog.bean.Systemlog;import com.tms.services.manage.systemlog.dao.SystemlogDao;import org.springframework.stereotype.Service;import javax.annotation.Resource;@Service("systemlogServiceManage")public class SystemlogServiceImpl extends ServersManager<Systemlog, SystemlogDao> implements        SystemlogService {    @Override    @Resource(name = "systemlogDaoManage")    public void setDao(SystemlogDao systemlogDao) {        this.dao = systemlogDao;    }    @Override	public Systemlog selectFirstOne(String account) {		return dao.selectFirstOne(account);	}}