package com.tms.web.action.manage.express;import com.tms.core.dao.page.PagerModel;import com.tms.services.manage.express.bean.Express;import com.tms.web.action.BaseController;import com.tms.services.manage.express.ExpressService;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.stereotype.Controller;import org.springframework.web.bind.annotation.RequestMapping;/** * 配送方式管理 * @author dylan */@Controller@RequestMapping("/manage/express/")public class ExpressAction extends BaseController<Express> {    private static final long serialVersionUID = 1L;    @Autowired    private ExpressService expressService;    public ExpressAction() {        super.page_toList = "/manage/express/expressList";        super.page_toEdit = "/manage/express/expressEdit";        super.page_toAdd = "/manage/express/expressEdit";    }    @Override    public ExpressService getService() {        return expressService;    }    @Override    protected void selectListAfter(PagerModel pager) {        pager.setPagerUrl("selectList");    }    @Override    public void insertAfter(Express e) {        e.clear();    }    public void setExpressService(ExpressService expressService) {        this.expressService = expressService;    }}